import React, { useState } from "react";
import ExpenseItem from "../Expenses/ExpenseItem";
import "../Expenses/expenses.css";
import ExpensesFilter from "../NewExpense/ExpensesFilter";
import Card from "../UI/Card";

function Expenses(props) {
  const [defaultYear, SetDefaultYear] = useState("2020");
  const changeYearHandler = (selectedYear) => {
    SetDefaultYear(selectedYear);
  };

  const filterByYear = props.items.filter(
    (expense) => expense.date.getFullYear().toString() === defaultYear
  );
  console.log(filterByYear);

  // let expenseComtent = <p>No Expenses</p>;
  if (expenseComtent.length > 0) {
    expenseComtent = filterByYear.map((expense) => (
      <ExpenseItem
        key={expense.id}
        title={expense.title}
        amount={expense.amount}
        date={expense.date}
      />
    ));
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          default={defaultYear}
          onChangeYear={changeYearHandler}
        />
        {expenseComtent}
      </Card>
    </div>
  );
}
export default Expenses;
